const UserList = UserList.sort((a,b) => a.points - b.points).map((user, i) => 
    <Table className="table table-striped table-dark">
        <thead>
            <tr>
                <th scope="col">#</th>
                <th scope="col">Rank</th>
                <th scope="col">Name</th>
                <th scope="col">Points</th>
            </tr>
        </thead>
        <tbody>
                <tr key={user.uid}>
                    <th scope="row">{i}</th>
                        <td>Rank</td>
                        <td>{user.name}</td>
                        <td>{user.points}</td>
                </tr>
            )}
        </tbody>
    </Table>
);

export default withFirebase(Leaderboards);
