import React, { Component } from 'react';
import '../../App.css';

import { withFirebase } from '../Firebase';
import { withAuthorization } from '../session';
import { compose } from 'recompose';

import { Button, Form } from 'react-bootstrap/';

import * as ROLES from '../constants/roles';

class EnterWins extends Component {
    constructor(props) {
        super(props);

        this.state = {
            results:"Here is where results will appear",
        };
    }

    componentDidMount(){
        document.addEventListener("keypress", 
          (tmp) => this.guessLetter(tmp.keyCode))
      }

    guessLetter = function(keypress) {
        var letter = String.fromCharCode(keypress.which);
        var num = 0;
        num = keypress.keycode;
            console.log(num);
    }

    render() {
        return (
            <div>
                <h1 className="pagePlaceholder">Admin - Enter Wins</h1>
                <div className="form-box">
                <Form>
                    <Form.Group>
                        <Form.Label>Username</Form.Label>
                        <Form.Control type="email" placeholder="Enter username to add points to" />
                        <Form.Text className="text-muted">
                            {this.state.results}
                        </Form.Text>
                        <Button variant="primary" type="submit">
                            Submit
                        </Button>
                    </Form.Group>
                </Form>
                </div>
            </div>
        );
    }
}

const condition = authUser =>
    authUser && !!authUser.roles[ROLES.ADMIN];

export default compose(
    withAuthorization(condition),
    withFirebase,
)(EnterWins);
