import React, { Component } from 'react';
import '../../App.css';

import { withFirebase } from '../Firebase';
import { withAuthorization } from '../session';
import { compose } from 'recompose';

import { Button, Form } from 'react-bootstrap/';

import * as ROLES from '../constants/roles';

class EnterWins extends Component {
    constructor(props) {
        super(props);

        this.state = {
            results:"Here is where results will appear",
            input: "",
        };
    }

    onChange(value) {
        this.setState({
            input: null
        })
    }

    componentDidMount(){
        document.getElementById("usernameBox").focus();
        document.addEventListener("keypress", 
        (tmp) => this.updateUser(tmp))
      }

    updateUser(event) {
        if (event.key === 13) {
            this.setState({
                input: this.inputNode.value
            })
            console.log(this.state.input);
        }
    }

    render() {
        return (
            <div>
                <h1 className="pagePlaceholder">Admin - Enter Wins</h1>
                <div className="form-box">
                <Form>
                    <Form.Group controlId="usernameBox">
                        <Form.Label>Username</Form.Label>
                        <Form.Control inputRef={node => this.inputNode = node} type="text"
                        placeholder="Enter username to add points to" />
                        <Form.Text id="userName" className="text-muted">
                            {this.state.results}
                        </Form.Text>
                        <Button variant="primary">
                            Submit
                        </Button>
                    </Form.Group>
                </Form>
                </div>
            </div>
        );
    }
}

const condition = authUser =>
    authUser && !!authUser.roles[ROLES.ADMIN];

export default compose(
    withAuthorization(condition),
    withFirebase,
)(EnterWins);
