import React, { Component } from 'react';
import '../../App.css';

import { withFirebase } from '../Firebase';
import { withAuthorization } from '../session';
import { compose } from 'recompose';

import { Button, Form } from 'react-bootstrap/';

import * as ROLES from '../constants/roles';

class EnterWins extends Component {
    constructor(props) {
        super(props);

        this.state = {
            results:"Here is where results will appear",
            value: '',
        };
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    componentDidMount(){
        document.getElementById("usernameBox").focus();
        document.addEventListener("keypress", 
        (tmp) => this.handleKeypress(tmp))
      }

    handleKeypress(event) {
        if (event.keyCode === 13) {
            document.getElementById("usernameBox").focus();
        }
    }

    updateUser = (event) => {
        event.preventDefault()
        var apiCall = this.state.value + "test";
        //Make API CALL HERE
        var tofetch = 'http://192.168.1.64:5000/express_backend/';
        fetch(tofetch)
        .then(response =>
            this.setState({ results: response},
            )
        );
        //console.log(apiCall)
        //End API call
        document.getElementById("usernameBox").focus();
        this.setState({value: ""})
    }

    render() {
        return (
            <div>
                <h1 className="pagePlaceholder">Admin - Enter Wins</h1>
                <div className="form-box">
                <Form id="formBox" onSubmit={this.updateUser}>
                    <Form.Group controlId="usernameBox">
                        <Form.Label>Username</Form.Label>
                        <Form.Control onChange={this.handleChange}
                        value={this.state.value}
                        placeholder="Enter username to add points to" />
                        <Form.Text id="userName" className="text-muted">
                            {this.state.results}
                        </Form.Text>
                        <Button type="submit" id="register" variant="primary">
                            Submit
                        </Button>
                    </Form.Group>
                </Form>
                </div>
            </div>
        );
    }
}

const condition = authUser =>
    authUser && !!authUser.roles[ROLES.ADMIN];

export default compose(
    withAuthorization(condition),
    withFirebase,
)(EnterWins);
